CREATE FUNCTION date_to_season(date_string date) RETURNS character varying
    LANGUAGE plpgsql IMMUTABLE
    AS $$
DECLARE
  month_string varchar;
BEGIN
  month_string := to_char(date_string, 'MM');
  CASE month_string
	WHEN '03'::bpchar THEN
		RETURN 'Spring';
	WHEN '06'::bpchar THEN
		RETURN 'Summer';
	WHEN '09'::bpchar THEN
		RETURN 'Autumn';
	WHEN '12'::bpchar THEN
		RETURN 'Winter';
	END CASE;
	RETURN 'Unknown season';
END
$$;


