CREATE FUNCTION vague_date_to_raw_string(date_start date, date_end date, date_type character varying) RETURNS character varying
    LANGUAGE plpgsql IMMUTABLE
    AS $$
DECLARE
  DAY_FORMAT CONSTANT varchar := 'YYYY-MM-DD';
BEGIN
  RETURN COALESCE(to_char(date_start, DAY_FORMAT), '') || '#' || COALESCE(to_char(date_end, DAY_FORMAT), '') || '#' || date_type;
END
$$;


