    oc.email_address,
    oc.updated_on,
    COALESCE(oc.person_name, ((((p.surname)::text || ', '::text) || (p.first_name)::text))::character varying) AS person_name,
    c.username,
    o.website_id,
    oc.created_on,
    oc.created_by_id,
