-- Name: grids_osgb_100k; Type: TABLE; Schema: indicia; Owner: indicia_user
--

CREATE TABLE grids_osgb_100k (
    square character varying(20) NOT NULL,
    geom public.geometry(Polygon,27700)
);


ALTER TABLE grids_osgb_100k OWNER TO indicia_user;

--
-- Name: grid_occurrences_osgb_100k; Type: VIEW; Schema: indicia; Owner: indicia_user
--

CREATE VIEW grid_occurrences_osgb_100k AS
 SELECT t.taxon,
    grid.square,
    grid.geom,
    o.id AS occurrence_id,
    s.id AS sample_id,
    ttl.id AS taxa_taxon_list_id,
    tl.title AS taxon_list
   FROM (((((occurrences o
     JOIN samples s ON ((s.id = o.sample_id)))
     JOIN grids_osgb_100k grid ON (public.st_intersects(grid.geom, public.st_transform(s.geom, 27700))))
     JOIN taxa_taxon_lists ttl ON ((ttl.id = o.taxa_taxon_list_id)))
     JOIN taxa t ON ((t.id = ttl.taxon_id)))
     JOIN taxon_lists tl ON ((tl.id = ttl.taxon_list_id)));


ALTER TABLE grid_occurrences_osgb_100k OWNER TO indicia_user;

