    t.public,
    (COALESCE(((p.first_name)::text || ' '::text), ''::text) || (p.surname)::text) AS created_by_name,
        CASE t.public
            WHEN true THEN NULL::integer
            ELSE u.id
        END AS private_for_user_id,
    t.deleted,
    logged_in.id AS user_id,
        CASE
            WHEN ((logged_in.core_role_id = 1) OR (u.id = logged_in.id)) THEN true
            ELSE false
        END AS edit_trigger,
        CASE
            WHEN (ta.id IS NULL) THEN true
            ELSE false
        END AS subscribe,
        CASE
            WHEN (ta.id IS NULL) THEN false
            ELSE true
        END AS edit_subscription
   FROM ((((triggers t
     JOIN users u ON (((u.id = t.created_by_id) AND (u.deleted = false))))
     JOIN people p ON (((p.id = u.person_id) AND (p.deleted = false))))
     CROSS JOIN users logged_in)
     LEFT JOIN trigger_actions ta ON (((ta.trigger_id = t.id) AND ((ta.param1)::text = ((logged_in.id)::character varying)::text) AND (ta.deleted = false))))
  WHERE (t.deleted = false);


